head.script:
// Define dataLayer and the gtag function.
window.dataLayer = window.dataLayer || [];
function gtag(){dataLayer.push(arguments);}

            gtag('consent', 'default', {
        'ad_storage': 'denied',
        'analytics_storage': 'denied'
    });
    window['disableYaCounter92496375'] = true;
        // Default ad_storage to 'denied'.

function consentGranted(ad, analytics) {
                gtag('consent', 'update', {
        'ad_storage': ad ? 'granted' : 'denied',
        'analytics_storage': analytics ? 'granted' : 'denied',
    });
    if(analytics){
        window['disableYaCounter92496375'] = false;
        var ee = setInterval(function () {
            if (typeof window.ym != 'undefined') {
                ym(92496375,"init",{clickmap:!0,trackLinks:!0,accurateTrackBounce:!0,webvisor:!0})
                clearInterval(ee);
            }
        }, 500);
    }
}
